function conductance(raw)
{
    let fraw = raw;
    let fcond = 0.0;
    let shortVal = trayConfig.tray.sensorShortVolts.value;
    let openVal = trayConfig.tray.refVolts.value;
    if (raw <= shortVal) fraw = shortVal + 0.0001;
    if (raw >= openVal)  fraw = openVal  - 0.0001;
    let traw = (fraw     - shortVal) / 3.3;
    let topen= (openVal - shortVal) / 3.3;
    fcond = trayConfig.tray.condMult.value * (topen * (1.0 - traw) - traw * (1.0 - topen)) / (topen * traw);
    if (fcond < 0.0 ) fcond = 0.0;
    if (fcond > 32766.0 ) fcond = 32766.0;
    fcond = Math.round(1000 * fcond) / 1000;
    return fcond; 
}

