let topicSplit = msg.topic.split('/');
let devEUI = topicSplit[2];
let traysConfig = flow.get('traysConfig');
if (traysConfig == undefined) return [null,null];
if (traysConfig.length < 1) return [null,null];
let trayConfig = null;
let trayIndex = -1;
for (let ii = 0; ii < traysConfig.length; ++ii)
{
    if (traysConfig[ii].tray.devEUI == devEUI)
    {
        trayConfig = traysConfig[ii];
        trayIndex = ii;
    }
}
if (trayConfig ==  null) return [null,null];

trayConfig.tray.timeStamp = new Date().getTime();

let dataString = msg.payload.data;
let ipos = 0;
let dataArray = [];
while ((ipos + 2) <= dataString.length)
{
    dataArray.push(parseInt(dataString.substring(ipos,ipos + 2),16));
    ipos = ipos + 2;
}
let temp = dataArray[5];
if (temp > 127) temp = 128 - temp;
if ( temp > 0)
{
    temp = temp + dataArray[6] / 100;
}
else
{
    temp = temp - dataArray[6] / 100;
}
temp = temp - trayConfig.tray.tempOffset.value;
temp = Math.round(100 * temp) / 100;
trayConfig.tray.temp.value = temp;
trayConfig.tray.humidity.value = dataArray[7];
trayConfig.tray.battery.value = dataArray[1];
trayConfig.tray.signal.value = -dataArray[2];
trayConfig.tray.watchdog.value = trayConfig.tray.watchdog.value + 1;
if (trayConfig.tray.watchdog.value > 32760) trayConfig.tray.watchdog.value = 1;

trayConfig.tray.lastUpdate.value = 0;
let newMsg = {topic:trayConfig.tray.name, payload:trayConfig.tray};
let archiveMsg = null;
let archiveTimes = flow.get('archiveTimes');
if ((trayConfig.tray.timeStamp - trayConfig.archiveTime) > trayConfig.tray.arcPeriod)
{
    trayConfig.archiveTime = trayConfig.tray.timeStamp;
    archiveMsg = newMsg;
}
return  [newMsg,archiveMsg];

